        String outputFormat = "yyyy-MM-dd";

        SimpleDateFormat inputFormatter = new SimpleDateFormat("MM/dd/yyyy hh:mm:ss a z");
        SimpleDateFormat outputFormatter = new SimpleDateFormat(outputFormat);

        try {
            // Parse input date string to Date object
            Date date = inputFormatter.parse(inputDate);

            // Format Date object to desired output format
            String formattedDate = outputFormatter.format(date);
log.error("end date after converting to desired formate :"+formattedDate);
            // Print the formatted date
           return formattedDate;
        } catch (ParseException e) {
