String peopleGroup = identity.getAttribute("peopleGroup");

  String description= ""; 
   if(peopleGroup !=null && peopleGroup.equalsIgnoreCase("RSC")){
	   		   description= "Office Users";
	   }
	 else if(peopleGroup !=null && peopleGroup.equalsIgnoreCase("Store Level")){
		description= "Store Level Users";
	   }
          else if(peopleGroup!=null && peopleGroup.equalsIgnoreCase("Outsource")){ 
		description= "Contractor";
}
  return description;
