Rule.Source:
  import sailpoint.tools.Util;
  import sailpoint.object.Identity;
  import sailpoint.object.Link;
  import sailpoint.object.Filter;

  import org.apache.log4j.Logger;
  import org.apache.log4j.Level;

  Logger logger = Logger.getLogger("rule.americana.AfterProvisioning");
  String ruleName = "Americana-Rule-IdentityAttribute-StartDate";

  String startDate = "";
  if (link != null) {
    String type = link.getAttribute("WORKER_TYPE");
    if (Util.isNullOrEmpty(type) ) {
      log.warn("User doesn't has the WORKER_TYPE "+link.getAttribute("PERSON_NUMBER"));
      return startDate;
    }
	if ("E".equalsIgnoreCase(type)) {
	  startDate = link.getAttribute("HIRE_DATE");
	} else if ("P".equalsIgnoreCase(type)) {
	  startDate = link.getAttribute("Projected_Start_Date");
	}
  }
  if (logger.isTraceEnabled()) {
    logger.trace("=====Exiting from Rule [ " +ruleName+ " ] startDate : "+startDate);
  }
  return startDate;
