import sailpoint.object.Custom;
import sailpoint.object.Identity;
import sailpoint.api.*;

boolean result = false;

if(previousIdentity != null && newIdentity != null){
String peopleGroup = newIdentity.getAttribute("peopleGroup");

String oldPosition = previousIdentity.getAttribute("PositionName");

String newPosition = newIdentity.getAttribute("PositionName");

String oldDepartment = previousIdentity.getAttribute("department");
String newDepartment = newIdentity.getAttribute("department");
String oldGradeCode = previousIdentity.getAttribute("gradeCode");
String newGradeCode = newIdentity.getAttribute("gradeCode");

if ((peopleGroup != null && peopleGroup.contains("RSC")) && ((oldPosition != null && !(oldPosition.equalsIgnoreCase(newPosition))) || (oldDepartment != null && !(oldDepartment.equalsIgnoreCase(newDepartment)))))
  result = true;
else if ((peopleGroup != null && peopleGroup.contains("RSC")) && (oldPosition != null && !(oldPosition.equalsIgnoreCase(newPosition)))) 
  result = true;
  else if ((peopleGroup != null && peopleGroup.contains("RSC")) && (oldGradeCode != null && !(oldGradeCode.equalsIgnoreCase(newGradeCode))))
  result = true;
 }
  return result;
