import sailpoint.object.Identity;
import sailpoint.object.*;
import sailpoint.api.*;
   import org.apache.log4j.Logger;
  import sailpoint.object.*;
 // Logger log=Logger.getLogger("customRule4");

            // Making a BIG assumption here that the nameid-format is unspecified/persitent

           // System.out.println("Assertion attr"+assertionAttributes);
            String nameId = (String)assertionAttributes.get("nameId");
//log.error("nameId :"+nameId);
            Identity ident;

            if(nameId != null) {

                // Lookup the identity based on nameId

QueryOptions qo=new QueryOptions();
Filter f=Filter.eq("email",nameId);
qo.addFilter(f);

            List idens = context.getObjects(Identity.class, qo);

ident=(Identity)idens.get(0);
//log.error("the ident is : "+ident);

            }

            return ident;
