String type = accountGroup.getType();



 String distinguishedName="";
String entDescription = "";
String displayName="";
entDescription = accountGroup.getAttribute("description");
distinguishedName= accountGroup.getAttribute("msDS-PrincipalName");
displayName=  accountGroup.getValue();

//String theDescription="distinguished name is" +distinguishedName+" and description is "+ entDescription ;
if(type.equalsIgnoreCase("group")){

if (Util.isNotNullOrEmpty(entDescription )) {

String entDescription = "Description : " +entDescription;
String value = "OU Details: "+displayName;

String newDescription = entDescription+"<br>"+value;
//String newDescription = "Description : " +entDescription+ "Value : "+displayName;



  accountGroup.addDescription(Locale.US.toString(), newDescription);
//log.error("account group theDescription is "+theDescription);

}

else
{
 accountGroup.addDescription(Locale.US.toString(), displayName);

}

}
return accountGroup;
