String type = accountGroup.getType();




String entDescription = "";

if(type.equalsIgnoreCase("azureADEligibleRole")||type.equalsIgnoreCase("servicePrincipal")||type.equalsIgnoreCase("servicePlan")||type.equalsIgnoreCase("azureADActiveRole")||type.equalsIgnoreCase("channel")||type.equalsIgnoreCase("azureActiveRole")||type.equalsIgnoreCase("azureEligibleRole")||type.equalsIgnoreCase("group")){
entDescription = accountGroup.getAttribute("displayName");
if (Util.isNotNullOrEmpty(entDescription )) {
     accountGroup.addDescription(Locale.US.toString(), entDescription);

}

}
return accountGroup;
