import java.text.SimpleDateFormat;
import java.util.Date;
public long timeDifference(String dateStart,String dateStop){
// Custom date format
log.error("inside method timeDifference");
SimpleDateFormat format = new SimpleDateFormat("yy/MM/dd HH:mm:ss");  

Date d1 = null;
Date d2 = null;
try {
d1 = format.parse(dateStart);
d2 = format.parse(dateStop);
} catch (ParseException e) {
e.printStackTrace();
}    

// Get msec from each, and subtract.
long diff = d2.getTime() - d1.getTime();
long diffSeconds = diff / 1000;         
long diffMinutes = diff / (60 * 1000);         
long diffHours = diff / (60 * 60 * 1000);
// return diffHours;
log.error("diff in mins::"+diffMinutes);
return diffMinutes;

}
String dateStart = new SimpleDateFormat("yy/MM/dd HH:mm:ss").format(new java.util.Date()); 
String dateStop = new SimpleDateFormat("yy/MM/dd").format(new java.util.Date())+" 20:00:00";
log.error("start date is:"+dateStart);
log.error("start date is:"+dateStop);

long minutes=timeDifference(dateStart,dateStop);
int num=(int)minutes;
GregorianCalendar currCal = (GregorianCalendar) GregorianCalendar.getInstance();        
currCal.add(Calendar.MINUTE, num);
log.error("Existing advanceTimeByMinutes");  
return currCal.getTime();
